新建add_api.js下载 express 和 body-parser 两个包
var express = require(&#x27;express&#x27;)var app = express()var func = require(&#x27;./add.js&#x27;)  // 导入js模块，并命名为funcvar bodyParser = require(&#x27;body-parser&#x27;);  // 导入请求体解析器// 调整参数大小限制，否则会提示参数过大。app.use(bodyParser.urlencoded(&#123;limit: &#x27;50mb&#x27;, extended: true&#125;));// 设置路由app.post(&#x27;/add&#x27;, function(req, res) &#123;    // 获取请求的真实IP	var ip = req.headers[&#x27;x-real-ip&#x27;] ? req.headers[&#x27;x-real-ip&#x27;] : req.ip.replace(/::ffff:/, &#x27;&#x27;);	// 获取请求时间	var time = new Date().toString().replace(/\+0800.*/, &#x27;&#x27;);	// 打印请求时间、IP、方法、路由	console.log(&#x27;INFO:&#x27;, time, ip, req.method, req.originalUrl, &#x27;200 OK!&#x27;);	// 获取POST请求的formdata	let result = req.body;	// let code = result.code;    // let seed = result.seed;    // let ts = result.ts;    console.log(&quot;result: &quot;, result);	console.log(&quot;num1: &quot;, result.num1);	console.log(&quot;num2: &quot;, result.num2);	// 调用cook模块中的get_cookie方法，该方法需要提前module.exports导出	var response = func.add(result.num1, result.num2);	// 设置响应头，如果不设置，通过asyncio_requests请求的res.json()会报错，因为它是根据响应头解析json数据	// 而requests可以直接使用res.json()解析，因为它是根据响应信息解析	res.set(&#x27;Content-Type&#x27;, &#x27;application/json&#x27;)	// 将JSON后的数据返回客户端	res.send(JSON.stringify(&#123;&quot;result&quot;: response&#125;));&#125;);app.listen(8919, () =&gt; &#123;	console.log(&quot;开启服务，端口8919&quot;, new Date().toString().replace(/\+0800.*/, &#x27;&#x27;))&#125;)

运行，用python写个post请求
